mapper.update:
update ${tableName} as table1 join ${tableName} as table2 on table1.sequence = table2.sequence+1
and table1.${fieldCode} = table2.${fieldCode}
set table1.sequence = table2.sequence, table2.sequence = table1.sequence where table1.id = #{id}

UPDATE ${tableName} as table1
JOIN ${tableName} as table2 on table2.sequence < table1.sequence

    and table1.${fieldCode} = table2.${fieldCode}

SET table1.sequence = table1.sequence - 1
where table2.id = #{id}
